// ssengine-core/src/model.rs\n// Core data structures for the spreadsheet engine\n\nuse std::collections::HashMap;\n\n// Basic type definitions\npub type RowId = u32;\npub type ColumnId = u32;\n\n// Cell address (row, column)\npub struct CellAddress {\n    pub row: RowId,\n    pub col: ColumnId,\n}\n\n// Cell value enum (simplified for now)\n#[derive(Debug, Clone)]\npub enum CellValue {\n    Blank,\n    Number(f64),\n    Text(String),\n    Boolean(bool),\n    // Will add Error and DateTime later\n}\n\n// Basic workbook structure\npub struct Workbook {\n    sheets: HashMap<String, Sheet>,\n}\n\nimpl Workbook {\n    pub fn new() -> Self {\n        Workbook {\n            sheets: HashMap::new(),\n        }\n    }\n    \n    pub fn sheet_count(&self) -> usize {\n        self.sheets.len()\n    }\n}\n\n// Sheet structure\npub struct Sheet {\n    name: String,\n    cells: HashMap<(RowId, ColumnId), Cell>,\n}\n\n// Cell structure\npub struct Cell {\n    pub value: CellValue,\n    pub formula: Option<String>,\n}
